$(function () { 
    var form = layui.form;
    var layer = layui.layer;
    form.verigy({
        nickname: function (value, item) { 
            if (value.length > 6) { 
                return '昵称长度必须在1-6个字符之间'
            }
        }
    })
    initUserInfo()
    function initUserInfo() {
        $.ajax({
            url: '/my/userinfo',
            method: 'GET',
            success: function (res) { 
                if (res.status !== 0) { 
                    return layer.msg('获取用户信息失败')
                }
                console.log(res)
                // 调用form.val()快速为表单赋值
                form.val('formUserInfo',res.data)
            }
        })
    }
    // 重置表单数据
    $('#btnReset').on('click', function (e) {
        // 阻止表单的默认提交行为
        e.preventDefault();
        initUserInfo();
    })
    $('.layui-form').on('submit', function (e) { 
        // 阻止表单的默认提交行为
        e.preventDefault();
        // 发起ajax数据请求
        $.ajax({
            url: '/my/userinfo',
            method: 'POST',
            success: function (res) { 
                if (res.status !== 0) { 
                    return layer.msg('更新用户信息失败')
                }
                layer.msg('更新用户信息成功')
                // 调用父页面的方法重新渲染用户的头像和信息
                // window为iframe代表的窗口,.parent就是父页面
                window.parent.getUserInfo()
            }
        })
    })
})
